function render() { 
  var dom = null;

  this.require('Modal').render(this);	

  $('#modal-modeljson').on('shown.bs.modal', function modal() {
    $('#modal-modeljson-name').focus();
  });

  // events
  dom = document.getElementById('modal-modeljson-name');
  dom.addEventListener('keyup', function keyup(event) {
    if (event.keyCode === 13) {
      event.stopPropagation();
      event.preventDefault();
      if ($('#modal-modeljson-name').val()) {
        this.ok();
      }
      return false;
    }
  }.bind(this));

  dom = document.getElementById('modal-modeljson-json');
  dom.addEventListener('keyup', function keyup(event) {
    if (event.keyCode === 13) {
      event.stopPropagation();
      event.preventDefault();
      if ($('#modal-modeljson-json').val()) {
        this.ok();
      }
      return false;
    }
  }.bind(this));
}
