12ab411c671f7c8.action:
function getAll() { 
  var store = localStorage.getItem('todos-system'),
      todos = {
        'Todo': {}
      },
      result = [],
      id = '';

	if (store) {
	  todos = JSON.parse(store);
	}

	for (id in todos.Todo) {
	  result.push(todos.Todo[id]);
	}

	return result;
}

