function click() { 
    var designer = this.require('designer'),
    message = this.require('message'),
    runWidow = this.require('designer').runWindow();

    $('#designer-toolbar-item-publish').tooltip('hide');
    if (designer.system()) {
        if (runWidow && !runWidow.closed) {
            runWidow.close();
            this.require('designer').runWindow(this.require('designer').open('app.html#' + designer.system().id(), 'app'));
            message.success('Running the system...');

            /*if (typeof require === 'undefined') {
                runWidow.document.location.reload();
                message.success('Running the system...');
            } else {
                // TODO 
                // to reload when implemented in electron
                message.info('<b>info</b>: to run again the system, click the widow where your system is running and go to the menu \'View > Refresh\â€™.');
            }*/
        } else {
            this.require('designer').runWindow(this.require('designer').open('app.html#' + designer.system().id(), 'app'));
            message.success('Running the system...');
        }
    } else {
        message.warning('You have no system to run.');
    }
}
