function click() {
    $('#designer-toolbar-item-export').tooltip('hide');
        var Dialog = this.require('DialogExport');
    var dialog = new Dialog({
        'title': 'Export the current system',
    });
    dialog.show();

    dialog.on('ok', function () {
        var message = this.require('message');
        // code inspired from the blog post
        // https://thiscouldbebetter.wordpress.com/2012/12/18/loading-editing-and-saving-a-text-file-in-html5-using-javascrip/
        if (this.require('designer').system()) {
            var system = null,
            textToWrite = null,
            textFileAsBlob = null,
            fileNameToSaveAs = null,
            downloadLink = null,
            js = '';
            source = '',
            DialogShare = this.require('DialogShare');

            system = this.require('db').collections().System.find({
                '_id': this.require('designer').system().id()
            })[0];
            system = JSON.parse(JSON.stringify(system));
            delete system.classInfo;

            if ($('#designer-dialog-export-url').prop("checked")) {
                this.hide();

                DialogShare = new DialogShare({
                    'title': 'Copy the link to the system'
                });
                DialogShare.show();
                DialogShare.on('ok', function () {
                    this.hide();
                });

            } else {

                if ($('#designer-dialog-export-json').prop("checked")) {
                    if ($('#designer-dialog-export-isSubSystem').prop("checked")) {
                      system.master = false;
                      system.subsystem = true;
                    }
                    textToWrite = JSON.stringify(system);
                    fileNameToSaveAs = this.require('designer').system().name() + '.json';
                }

                if ($('#designer-dialog-export-html').prop("checked")) {
                    js = decodeURIComponent(this.require('system-runtime.min.js').source());
                    source = this.require('export-app.html').source();
                    source = source
                                .replace(/{{library}}/g, js)
                                .replace(/{{library}}/g, function() {return '\$&'})
                                .replace(/{{name}}/g, this.require('designer').system().name())
                                .replace(/{{system}}/g, JSON.stringify(system));
                    textToWrite = source;
                    fileNameToSaveAs = this.require('designer').system().name() + '.html';
                }

                if ($('#designer-dialog-export-node').prop("checked")) {
                    source = decodeURIComponent(this.require('app.js').source());
                    source = source
                                .replace(/{{name}}/g, this.require('designer').system().name())
                                .replace(/{{system}}/g, JSON.stringify(system));
                    textToWrite = source;
                    fileNameToSaveAs = this.require('designer').system().name() + '.js';
                }

            	textFileAsBlob = new Blob([textToWrite], {
            	    type:'text/plain'
            	});
            	downloadLink = document.createElement('a');

            	downloadLink.download = fileNameToSaveAs;
            	downloadLink.innerHTML = 'Download File';
            	if (window.webkitURL != null) {
            		downloadLink.href = window.webkitURL.createObjectURL(textFileAsBlob);
            	} else {
            		downloadLink.href = window.URL.createObjectURL(textFileAsBlob);
            		downloadLink.onclick = function (event) {
            		    document.body.removeChild(event.target)
            		};
            		downloadLink.style.display = 'none';
            		document.body.appendChild(downloadLink);
            	}

            	downloadLink.click();
            	this.hide();
            }

        } else {
            message.warning('you have no system to export.')
        }
    });
}
