function click() {
    $('#designer-toolbar-item-copyright').tooltip('hide');
    var DialogCopyright = this.require('DialogCopyright');
    var message = this.require('copyright.html');

    DialogCopyright = new DialogCopyright({
        'title': 'System Designer',
        'message': message.source().replace('{{version}}', this.require('designer').version())
    });
    DialogCopyright.show();
    DialogCopyright.on('ok', function () {
        this.hide();
    });
}
