function click() {
    $('#designer-toolbar-item-export-document').tooltip('hide');

    var data = this.require('designer').store().data();
    var doc = {
        "name": "schema " + data._name,
        "master": false,
        "subsystem": true,
        "version": "0.0.1",
        "description": "Schema " + data._name,
        "schemas": {},
        "models": {},
        "behaviors": {},
        "types": {},
        "components": {}
    };

    // clean
    doc.name = doc.name.trim();
    doc.name = doc.name.replace(/ /gi, '-');
    doc.name = doc.name.toLocaleLowerCase();

    doc.schemas[data._id] = data;

    this.require('designer').saveAs(doc, 'schema.' + data._name.replace(/ /gi, '-').toLowerCase() + '.json');
}
