function click() {
    $('#designer-toolbar-item-export-document').tooltip('hide');

    var data = this.require('designer').store().data();
    var name = data._id;
    var model = this.require('designer').store().collection();
    var doc = {
        "name": "component " + name,
        "master": false,
        "subsystem": true,
        "version": "0.0.1",
        "description": "Component " + name,
        "schemas": {},
        "models": {},
        "behaviors": {},
        "types": {},
        "components": {}
    };

    // clean
    doc.name = doc.name.trim();
    doc.name = doc.name.replace(/ /gi, '-');
    doc.name = doc.name.toLocaleLowerCase();

    if (typeof doc.models[model]) {
        doc.components[model] = {};
    }

    doc.components[model][data._id] = data;

    this.require('designer').saveAs(doc, name.replace(/ /gi, '-').toLowerCase() + '.json');
}
