function open(url, target, isExt) {
    var ref = null;

    function _getRelativePath() {
      var result = '',
          paths = [];

      paths = window.location.href.split('/');
      result = paths[paths.length - 1];

      return result;
    }

    switch(true) {
      case typeof require !== 'undefined':
        if (isExt) {
            ref = require('electron').shell.openExternal(url ,target);  
        } else {
            ref = window.open(url, target);
        }
        break;
      case typeof cordova !== 'undefined':
        if (isExt) {
          window.open(url, '_system');
        } else { 
          document.location.href = url;
        }
        break;
      default:
        ref = window.open(url, target);
        break;
    }

    this.require('storage').set('system-designer-last-context', _getRelativePath());

    return ref;
}
