function success(message) {
  var html = this.require('message-alert-success.html').source(),
      id = this.require('designer').generateId(),
      domId = 'designer-message-success-' + id;

  document.querySelector('#designer-message').insertAdjacentHTML('afterbegin', 
    html.replace(/{{message}}/gi, message)
        .replace(/{id}/gi, id)
    );
  $('#' + domId).fadeIn(500);

  setTimeout(function () {
    $('#' + domId).fadeOut(1000, function() {
      $(this).remove();
    });
  }, 4000);
}
