function render() {
  let template = '';
  let message = '';
  let dom = null;
  let menu = this.require('state').menu();
  let system = this.require('factory').system();

  template = this.require('message-help.html');

  switch (true) {
    case menu === 'systems':
      message = "To create a system, click on the '+' button or click here.";
      break;
    case menu === 'schemas' && typeof system !== 'undefined':
      message = "To create a schema, click on the '+' button or click here.";
      break;
    case menu === 'types' && typeof system !== 'undefined':
      message = "To create a type, click on the '+' button or click here.";
      break;
    case menu === 'behaviors' && typeof system !== 'undefined':
      message = "To create a behavior, click on the '+' button or click here.";
      break;  
    case menu === 'components' && typeof system !== 'undefined':
      message = "To create a component, click on the '+' button or click here.";
      break;
    default:
      break;
  }

  document.querySelector('#designer-workspace').insertAdjacentHTML(
    'afterbegin', 
    template.source().replace(/{{message}}/gi, message));

  // events
  dom = document.getElementById('panel-message-help');
  if (dom) {
    dom.addEventListener('click', function click(event) {
      this.require('workspace').create();
    }.bind(this));
  }

}
