function click() {
    var debugWindow = this.require('designer').debugWindow(),
        config = this.require('storage').get('system-designer-config'),
        message = this.require('message');

    $('#designer-toolbar-item-refresh').tooltip('hide');
    if (config) {
        if (typeof config.debugType !== 'undefined' && config.debugType === 'client') {
            if (config.urlClient) {
                if (debugWindow && !debugWindow.closed) {
                    message.success('Searching for a system...');
                    this.require('channel').$designerSync();
                } else {
                    this.require('designer').debugWindow(this.require('designer').open(config.urlClient, 'debug'));
                }
            } else {
                message.warning('No url set. Please set the url in the configuration panel.');
            }
        }
        if (typeof config.debugType !== 'undefined' && config.debugType === 'server') {
            if (config.urlServer) {
                message.success('Searching for a server...');
                var self = this;
                $.getJSON(config.urlServer + ':8888/$designerSync', function (system) {
                    var Dialog = null,
                        dialog = null;

                    Dialog = self.require('DialogImport');
                    dialog = new Dialog({
                        'title': 'A system has been found',
                        'message': 'Do you wan to import it ?',
                        'data': system
                    });
                    dialog.show();

                    dialog.on('ok', function () {
                      var System = this.require('System'),
                          sys = null,
                          designer = this.require('designer'),
                          message = this.require('message');

                      if (designer.system()) {
                          designer.system().destroy();
                      }
                      sys = new System(this.data());
                      designer.system(sys);

                      // empty log
                      designer.logs().forEach(function (item) {
                          this.logs().pop();
                      }.bind(designer));

                      designer.save();

                      designer.space(sys.name());
                      designer.spaces().render();
                      designer.workspace().render();

                      designer.updateRouter();

                      this.hide();
                      designer.save();

                      message.success('Importation of the system is done.');
                    });
                });
            } else {
                message.warning('No url set. Please set the url in the configuration panel.');
            }
        }
        if (typeof config.debugType === 'undefined') {
            message.warning('No url set. Please set the url in the configuration panel.');
        }
    } else {
        message.warning('No url set. Please set the url in the configuration panel.');
    }
}

