function click() {
  $('#designer-toolbar-item-search').tooltip('hide');

  var message = this.require('message');
  var Dialog = this.require('DialogSearch');

  if (this.require('designer').system()) {
    var dialog = new Dialog({
        'title': 'Search in the system',
    });
    dialog.show();

    dialog.on('ok', function click() {
      this.hide();
    });

    dialog.on('filter', function filter(value) {
      var systemId = this.require('designer').system().id(),
          schemas = this.require('designer').system().schemas(),
          models = this.require('designer').system().models(),
          types = this.require('designer').system().types(),
          behaviors = this.require('designer').system().behaviors(),
          components = this.require('designer').system().components(),
          collection = '',
          id = '',
          toSearch = '',
          index = -1,
          result = [];

      // clean
      document.getElementById('designer-dialog-search-result').innerHTML = '';

      if (value.length > 0 && value.trim() !== '') {
        if (this.inContent()) {

          // schema
          for (id in schemas) {
            toSearch = JSON.stringify(schemas[id]);
            index = toSearch.toLowerCase().indexOf(value.toLowerCase());
            if (index !=-1) {
              link = '<a href="#' + systemId + '#schemas#' + id + '" class="list-group-item" class="list-group-item active">' +
                '<h4 class="list-group-item-heading">Schema ' + schemas[id]._name + '</h4>' +
                '<p class="list-group-item-text">...' + toSearch.substring(index - 20, index + value.length + 20) + '...</p>' +
                '</a>';

              result.push(link);
            }
          }

          // model
          for (id in models) {
            toSearch = JSON.stringify(models[id]);
            index = toSearch.toLowerCase().indexOf(value.toLowerCase());
            if (index !=-1) {
              link = '<a href="#' + systemId + '#models#' + id + '" class="list-group-item" class="list-group-item active">' +
                '<h4 class="list-group-item-heading">Model ' + models[id]._name + '</h4>' +
                '<p class="list-group-item-text">...' + toSearch.substring(index - 20, index + value.length + 20) + '...</p>' +
                '</a>';

              result.push(link);
            }
          }

          // type
          for (id in types) {
            toSearch = JSON.stringify(types[id]);
            index = toSearch.toLowerCase().indexOf(value.toLowerCase());
            if (index !=-1) {
              link = '<a href="#' + systemId + '#types#' + types[id].name + '" class="list-group-item" class="list-group-item active">' +
                '<h4 class="list-group-item-heading">Type ' + types[id].name + '</h4>' +
                '<p class="list-group-item-text">...' + toSearch.substring(index - 20, index + value.length + 20) + '...</p>' +
                '</a>';

              result.push(link);
            }
          }

          // behavior
          for (id in behaviors) {
            toSearch = behaviors[id].action;
            index = toSearch.toLowerCase().indexOf(value.toLowerCase());
            if (index !=-1) {
              link = '<a href="#' + systemId + '#behaviors#' + behaviors[id].component + '#' + behaviors[id].state +'" class="list-group-item" class="list-group-item active">' +
                '<h4 class="list-group-item-heading">Behavior ' + behaviors[id].component + '.' + behaviors[id].state + '</h4>' +
                '<p class="list-group-item-text">...' + toSearch.substring(index - 20, index + value.length + 20) + '...</p>' +
                '</a>';

              result.push(link);
            }
          }

          // components
          for (collection in components) {
            for (id in components[collection]) {
              toSearch = JSON.stringify(components[collection][id]);
              index = toSearch.toLowerCase().indexOf(value.toLowerCase());
              if (index !=-1) {
                link = '<a href="#' + systemId + '#components#' + collection + '#' + id +'" class="list-group-item" class="list-group-item active">' +
                  '<h4 class="list-group-item-heading">Component ' + components[collection][id]._id + '</h4>' +
                  '<p class="list-group-item-text">...' + toSearch.substring(index - 20, index + value.length + 20).replace('<', '&lt;') + '...</p>' +
                  '</a>';

                result.push(link);
              }
            }
          }

        } else {
          // schema
          for (id in schemas) {
            if (schemas[id]._name.toLowerCase().indexOf(value.toLowerCase()) !== -1) {
              result.push('<a href="#' + systemId + '#schemas#' + id + '" class="list-group-item"><h4 class="list-group-item-heading">Schema ' + schemas[id]._name + '</h4></a>');
            }
          }

          // model
          for (id in models) {
            if (models[id]._name.toLowerCase().indexOf(value.toLowerCase()) !== -1) {
              result.push('<a href="#' + systemId + '#models#' + id + '" class="list-group-item"><h4 class="list-group-item-heading">Model ' + models[id]._name + '</h4></a>');
            }
          }

          // type
          for (id in types) {
            if (types[id].name.toLowerCase().indexOf(value.toLowerCase()) !== -1) {
              result.push('<a href="#' + systemId + '#types#' + types[id].name + '" class="list-group-item"><h4 class="list-group-item-heading">Type ' + types[id].name + '</h4></a>');
            }
          }

          // behaviors
          for (id in behaviors) {
            if (behaviors[id].state.toLowerCase().indexOf(value.toLowerCase()) !== -1) {
              result.push('<a href="#' + systemId + '#behaviors#' + behaviors[id].component + '#' + behaviors[id].state +'" class="list-group-item"><h4 class="list-group-item-heading">Behavior ' + behaviors[id].component + '.' + behaviors[id].state + '</h4></a>');
            }
          }

          // components
          for (collection in components) {
            for (id in components[collection]) {
              if (components[collection][id]._id.toLowerCase().indexOf(value.toLowerCase()) !== -1) {
                result.push('<a href="#' + systemId + '#components#' + collection + '#' + id +'" class="list-group-item" class="list-group-item"><h4 class="list-group-item-heading">Component ' + components[collection][id]._id + '</h4></a>');
              }
            }
          }
        }

        // result
        document.getElementById('designer-dialog-search-result').innerHTML = result.join(' ');
      }
    });
  } else {
    message.warning('There is no system.');
  }
}

