function insert(event) { 
  var logs = [],
      lastInsertOrder = -1;

  if ($metamodel.inheritFrom(event.collection, 'SyncComponent')) {

    // search if last insert action before this one 
    // was a message from the server or not
    logs = $db.RuntimeLog.find({
      'action': 'insert',
      'id': event.document._id
    });

    if (logs.length) {
      logs.sort(function sort(logA, logB) {
        return logA.order - logB.order;
      });
      lastInsertOrder = logs[0].order;
    }

    logs = $db.RuntimeLog.find({
      'order': lastInsertOrder - 1,
      'collection': 'RuntimeMessage'
    });

    switch (true) {
      case !logs[0]:
      case logs[0] && 
           logs[0].value.event !== 'serverDatabaseInsert':
      case logs[0] && logs[0].value.event === 'serverDatabaseInsert' && 
           logs[0].value.data[0].document._id !== event.document._id:

        // send the event to another system   
        this.require('channel').clientDatabaseInsert(event);
        break;
      default:
        break;
    }
  }
}

