function serverDatabaseUpdate(event) { 
  var update = {},
      collection = this.require('db').collections()[event.collection],
      result = [],
      doc = null;

  result = collection.find({
    '_id': event.id
  });
  doc = result[0];

  update[event.field] = event.value;
  if (doc[event.field] !== event.value) {
    collection.update({
        '_id': event.id
      },
      update
    );	
  }
}

