function get(collection, id) { 
  var restClient = this.require('restClient'),
      url = 'http://',
      systemId = this.require('runtime').system().id();

  if (restClient.ssl()) {
    url = 'https://';
  }
  url = url + restClient.ip() + ':' + restClient.port() + '/' + restClient.apiName() + '/';
  url = url.replace(/\/\//g, '/');
  if (id) {
    url = url + collection + '/' + id;
  } else {
    url = url + collection;
  }

  fetch(url, {
    method: 'GET',
    headers: new Headers({
	    'Content-Type': 'application/json',
	    'X-System-Id': systemId,
	    'X-System-Event': 'clientGet'
    })
  }).then(function (response) {
    response.json().then(function (response) {
      // a message has arrived from another system
      runtime.message(response);
    });
  });
}

