function info(message) { 
  if (this.level() === 'info' || this.level() === 'debug') {
    var date = new Date(),
        time = date.toTimeString(),
        ms = date.getMilliseconds();

    time = time.split(' ')[0].trim();
    time = time.replace(':', 'h');
    time = time.replace(':', 'm');
    time = time.split('m')[0].trim();
    time = time + ':' + ms;

    this.require('channel').$appLogInfo('[' + time + '] ' + message);
  }	
}

